package com.amitinside.swt.practice.ch3;import org.eclipse.swt.SWT;import org.eclipse.swt.widgets.Display;import org.eclipse.swt.widgets.Label;import org.eclipse.swt.widgets.Shell;public class HelloWorld {	public static void main(String[] args) {		final Display display = Display.getDefault();		final Shell shell = new Shell(display, SWT.CLOSE | SWT.CENTER | SWT.YES);		final Label label = new Label(shell, SWT.CENTER);		label.setText("Hello, World");		label.setBounds(shell.getClientArea());		shell.open();		while (!shell.isDisposed()) {			if (!display.readAndDispatch()) {				display.sleep();			}		}		display.dispose();	}}